REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","52","1","1","9","2001-07-30","2001","Deparia cataracticola (Woodsiaceae), a New Species from Hawaii","A new Hawaiian fern, Deparia cataracticola (Woodsiaceae), is described. It is morphologically distinct and ecologically unique among the Hawaiian species of Deparia. A molecular phylogenetic analysis showed that D. cataracticola is sister to a clade of D. marginalis and D. prolifera.","APG : Acta phytotaxonomica et geobotanica","KATO MASAHIRO","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0004901194","http://ci.nii.ac.jp/naid/110003733643","http://ci.nii.ac.jp/naid/110003733643");
