REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","52","1","35","40","2001-07-30","2001","Tricyrtis chinensis Hir. Takah. (Liliaceae), A New Species from Southeast China","Chinese plants previously identified as Tricyrtis macropoda Miq. are distinct from Japanese plants of that species in several characteristics. We therefore recognize them as a new species, Tricyrtis chinensis, and provide a key to the species of the section Tricyrtis, to which the new species is assigned.","APG : Acta phytotaxonomica et geobotanica","TAKAHASHI HIROSHI;QIN XIANG-KUNG;KONTA FUMIHIRO","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0004901196","http://ci.nii.ac.jp/naid/110003733645","http://ci.nii.ac.jp/naid/110003733645");
