REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","52","2","97","102","2001-10-31","2001","Cladopus javanicus (Podostemaceae), a New Species from Java","A new species of Podostemaceae, Cladopus javanicus, is described from West Java. It is a second Malesian species of Cladopus and differs from the other congeneric species including Cladopus nymanii and Torrenticola queenslandica in the long shoots and densely imbricate digitate leaves.","APG : Acta phytotaxonomica et geobotanica","KATO MASAHIRO;HAMBALI GREGORI G.","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0004901208","http://ci.nii.ac.jp/naid/110003733652","http://ci.nii.ac.jp/naid/110003733652");
