REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","55","2","79","88","2004-08-25","2004","Musa voonii, a New Musa Species from Northern Borneo and Discussion of the Section Callimusa in Borneo","A new wild banana species, Musa voonii Hakkinen, is described and illustrated. The species is abundant in the Lawas area, Sarawak, East Malaysia and in isolated locations within a radius of 200km from Lawas. Remarks on the sect. Callimusa in Borneo are presented.","APG : Acta phytotaxonomica et geobotanica","HAKKINEN MARKKU","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008323256","http://ci.nii.ac.jp/naid/110006318323","http://ci.nii.ac.jp/naid/110006318323");
