REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","57","3","199","203","2007-01-10","2007","Tricyrtis pseudolatifolia Hir. Takah. et H. Koyama (Liliaceae), A New Species from Central China","Plants in central China often identified as Tricyrtis latifolia are distinct from that species in many characteristics: the perianth is nearly horizontally patent at the level of 1/3 from the base (obliquely ascending in T. latifolia); the anthers are much longer than those of T. latifolia; the inner tepals are narrowly trullate at the base (auriculate in T. latifolia); the roots contain orange anthraquinoid pigments; the leaves are pubescent on both surfaces (glabrous on both surfaces or somewhat pubescent only in the undersurface in T. latifolia). We therefore describe the central Chinese plants as a new species, T. pseudolatifolia, which we assigned to section Tricyrtis, to which T. latifolia belongs.","APG : Acta phytotaxonomica et geobotanica","TAKAHASHI HIROSHI;KOYAMA HIROSHIGE","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008323313","http://ci.nii.ac.jp/naid/110006318351","http://ci.nii.ac.jp/naid/110006318351");
REPLACE INTO multilingual(`key`,language,value,guid) values("title","en","Tricyrtis pseudolatifolia Hir. Takah. et H. Koyama (Liliaceae), A New Species from Central China","http://ci.nii.ac.jp/naid/110006318351");
REPLACE INTO multilingual(`key`,language,value,guid) values("abstract","en","Plants in central China often identified as Tricyrtis latifolia are distinct from that species in many characteristics: the perianth is nearly horizontally patent at the level of 1/3 from the base (obliquely ascending in T. latifolia); the anthers are much longer than those of T. latifolia; the inner tepals are narrowly trullate at the base (auriculate in T. latifolia); the roots contain orange anthraquinoid pigments; the leaves are pubescent on both surfaces (glabrous on both surfaces or somewhat pubescent only in the undersurface in T. latifolia). We therefore describe the central Chinese plants as a new species, T. pseudolatifolia, which we assigned to section Tricyrtis, to which T. latifolia belongs.","http://ci.nii.ac.jp/naid/110006318351");
