REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","57","3","233","236","2007-01-10","2007","A New Record of Gmelina tomentosa Fletcher (Verbenaceae) from Myanmar","Gmelina tomentosa (Verbenaceae) is newly reported from Myanmar. Until now, this species has been known only from the original description and collected only from Thailand. Based on the new materials, a full description and an illustration are presented here.","APG : Acta phytotaxonomica et geobotanica","TANAKA NOBUYUKI;NAGAMASU HIDETOSHI","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008323316","http://ci.nii.ac.jp/naid/110006318354","http://ci.nii.ac.jp/naid/110006318354");
