REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","56","1","21","26","2005-04-11","2005","Plant Inventory Research : Contributions to the Flora of Myanmar","The Makino Botanical Garden and its associated institutions have been conducting plant inventory research for contributions to the Flora of Myanmar, based on a memorandum of understanding signed by Makino and the Forest Department, Ministry of Forestry of Myanmar. Current our inventory field research of Myanmar is presented here. Field studies have focused on 2 sites, Mt. Popa and Natma Taung (Mt. Victoria) National Park. Checklists to the flowering plants of these two mountains are currently being prepared respectively based on the collections we have amassed so far. We have also organized training programs to help build capacity amongst our counterparts in Myanmar. For the continued success of collaborative floristic work in Myanmar, a local research centre is required to serve as a herbarium and to promote education on issues such as taxonomic botany.","APG : Acta phytotaxonomica et geobotanica","TANAKA NOBUYUKI","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008323351","http://ci.nii.ac.jp/naid/110006318373","http://ci.nii.ac.jp/naid/110006318373");
REPLACE INTO multilingual(`key`,language,value,guid) values("title","en","Plant Inventory Research : Contributions to the Flora of Myanmar","http://ci.nii.ac.jp/naid/110006318373");
REPLACE INTO multilingual(`key`,language,value,guid) values("abstract","en","The Makino Botanical Garden and its associated institutions have been conducting plant inventory research for contributions to the Flora of Myanmar, based on a memorandum of understanding signed by Makino and the Forest Department, Ministry of Forestry of Myanmar. Current our inventory field research of Myanmar is presented here. Field studies have focused on 2 sites, Mt. Popa and Natma Taung (Mt. Victoria) National Park. Checklists to the flowering plants of these two mountains are currently being prepared respectively based on the collections we have amassed so far. We have also organized training programs to help build capacity amongst our counterparts in Myanmar. For the continued success of collaborative floristic work in Myanmar, a local research centre is required to serve as a herbarium and to promote education on issues such as taxonomic botany.","http://ci.nii.ac.jp/naid/110006318373");
