REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","56","1","27","31","2005-04-11","2005","Musa azizii, a New Musa Species (Musaceae) from Northern Borneo","A new wild banana species, Musa azizii Hakkinen, is described and illustrated. The species is extremely rare, originating in the Lumut Range area of Sarawak, East Malaysia.","APG : Acta phytotaxonomica et geobotanica","HAKKINEN MARKKU","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008323352","http://ci.nii.ac.jp/naid/110006318374","http://ci.nii.ac.jp/naid/110006318374");
