REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","54","1","81","83","2003-06-09","2003","A Significant Range Extension for the Monotypic Tamijioideae (Zingiberaceae)","A range extension for Tamijia flagellaris is reported from the upper Tatau River in Bintulu District, Sarawak, Malaysia. A brief description of the fruit, which was unknown prior to this report, is also given.","APG : Acta phytotaxonomica et geobotanica","SAKAI SHOKO;NAGAMASU HIDETOSHI","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008323615","http://ci.nii.ac.jp/naid/110006318618","http://ci.nii.ac.jp/naid/110006318618");
