REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","56","3","207","212","2005-12-26","2005","Didymoplexiella cinnabarina (Orchidaceae) : a New Species from Muller Range, Central Kalimantan, Indonesia","A new species of the genus Didymoplexiella (Orchidaceae), D. cinnabarina, is described and illustrated. This saprophytic/mycoheterotrophic orchid was found in the Muller Range, Central Kalimantan, Indonesia. A combination of several characters, such as long, decurved stelidia, the absence of a column foot, a trilobed lip, and clear coloration, indicates that this species belongs to the genus Didymoplexiella. It is clearly distinct from other known species of Didymoplexiella in the vermilion-red coloration of the entire inflorescence and flowers, and the moderate size of its flowers. It most resembles D. kinabaluensis, which was collected from Menetendok Gorge, Kinabalu, Sabah, Borneo, but differs in color and lip morphology. This species is the eighth species of the genus reported so far.","APG : Acta phytotaxonomica et geobotanica","TSUKAYA HIROKAZU;NAKAJIMA MUTSUKO;OKADA HIROSHI","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008323658","http://ci.nii.ac.jp/naid/110006318638","http://ci.nii.ac.jp/naid/110006318638");
REPLACE INTO multilingual(`key`,language,value,guid) values("title","en","Didymoplexiella cinnabarina (Orchidaceae) : a New Species from Muller Range, Central Kalimantan, Indonesia","http://ci.nii.ac.jp/naid/110006318638");
REPLACE INTO multilingual(`key`,language,value,guid) values("abstract","en","A new species of the genus Didymoplexiella (Orchidaceae), D. cinnabarina, is described and illustrated. This saprophytic/mycoheterotrophic orchid was found in the Muller Range, Central Kalimantan, Indonesia. A combination of several characters, such as long, decurved stelidia, the absence of a column foot, a trilobed lip, and clear coloration, indicates that this species belongs to the genus Didymoplexiella. It is clearly distinct from other known species of Didymoplexiella in the vermilion-red coloration of the entire inflorescence and flowers, and the moderate size of its flowers. It most resembles D. kinabaluensis, which was collected from Menetendok Gorge, Kinabalu, Sabah, Borneo, but differs in color and lip morphology. This species is the eighth species of the genus reported so far.","http://ci.nii.ac.jp/naid/110006318638");
