REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","58","2","83","86","2007-11-30","2007","Begonia (sect. Sphenanthera) hayamiana (Begoniaceae), a New Species from Northern Myanmar","Begonia (sect. Sphenanthera) hayamiana Nb. Tanaka (Begoniaceae), a new species from northern Myanmar with fleshy fruits, is described and illustrated. Begonia hayamiana is characterized by a combination of nearly symmetric leaves that are truncate at the base, and 4-locular fruit. It is known only from the type locality in the Hukaung Valley Tiger Reserve, Kachin State.","APG : Acta phytotaxonomica et geobotanica","TANAKA NOBUYUKI;HUGHES MARK","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008520652","http://ci.nii.ac.jp/naid/110006533807","http://ci.nii.ac.jp/naid/110006533807");
