REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","59","1","73","77","2008-03-25","2008","Liparis purpureovittata (Orchidaceae) : a New Species from Japan","A new species, Liparis purpureovittata (Orchidaceae), is described from central and northern Japan on the basis of both morphological and molecular characters. This species is distinguishable from the closely related Liparis kumokiri in the abruptly dilated labellum (vs. the gradually dilated labellum in L. kumokiri), the labellum recurved strongly in the middle (vs. the labellum strongly recurved in both the middle and distal part), and the column with rounded marginal wings (vs. angular marginal column wings).","APG : Acta phytotaxonomica et geobotanica","TSUTSUMI CHIE;YUKAWA TOMOHISA;KATO MASAHIRO","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008665877","http://ci.nii.ac.jp/naid/110006649700","http://ci.nii.ac.jp/naid/110006649700");
