REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","59","2","179","181","2008-07-30","2008","Eria carinata (Orchidaceae), Newly Recorded from the Malay Peninsula","Eria carinata Gibson ex Lindl. (Orchidaceae), thus far recorded from Sikkim-Himalaya to Thailand and Vietnam, is newly recorded for peninsular Malaysia. This new find marks the southernmost limit of distribution for the species.","APG : Acta phytotaxonomica et geobotanica","TANAKA NOBUYUKI;YUKAWA TOMOHISA","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008799217","http://ci.nii.ac.jp/naid/110006873841","http://ci.nii.ac.jp/naid/110006873841");
