REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","59","3","223","227","2008-11-28","2008","A New Species of Ledermanniella (Podostemaceae) from Cameroon","A new species of Podostemaceae subfamily Podostemoideae, Ledermanniella ntemensis, is described from southwestern Cameroon. It is characterized by the sparse scales on the shoot branches and the three stigmas.","APG : Acta phytotaxonomica et geobotanica","KITA YOKO;KOI SATOSHI;RUTISHAUSER ROLF;KATO MASAHIRO","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008945883","http://ci.nii.ac.jp/naid/110007022760","http://ci.nii.ac.jp/naid/110007022760");
