REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","59","3","219","222","2008-11-28","2008","A New Species of Neottia (Orchidaceae) from the Tanzawa Mountains, Japan","A new species, Neottia inagakii Yagame, Katsuy. & T. Yukawa, is described from the Tanzawa Mountains, Kanagawa Prefecture, Japan. Neottia inagakii is morphologically similar to N. kiusiana but can be distinguished by the smaller and nearly closed flower, the adaxially folded, 2- or 3-lobed epichile of the lip, the erect anther lacking a filament, and the cylindrical column.","APG : Acta phytotaxonomica et geobotanica","YAGAME TAKAHIRO;KATSUYAMA TERUO;YUKAWA TOMOHISA","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008945882","http://ci.nii.ac.jp/naid/110007022759","http://ci.nii.ac.jp/naid/110007022759");
