REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","60","3","159","162","2010-03-30","2010","A New Species of Pieris (Ericaceae) from Amamioshima, Ryukyu Islands, Japan","A new species of Pieris (Ericaceae), P. amamioshimensis Setoguchi & Y. Maeda, from Amamioshima, Ryukyu Islands, Japan, is described and illustrated. Pieris amamioshimensis is similar to P. koidzumiana from Okinawa Island, but differs in having lanceolate-oblanceolate leaves and larger corollas.","APG : Acta phytotaxonomica et geobotanica","SETOGUCHI HIROAKI;MAEDA YOSHIYUKI","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0009410644","http://ci.nii.ac.jp/naid/110007593080","http://ci.nii.ac.jp/naid/110007593080");
