REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","60","3","167","170","2010-03-30","2010","Doritis natmataungensis (Orchidaceae), a New Species from Myanmar","A new species, Doritis natmataungensis T. Yukawa, Nb. Tanaka & J. Murata (Orchidaceae) is described from Myanmar. It is easily distinguished from the closely related Doritis stobartiana (Rchb. f.) T. Yukawa & K. Kita by the following combination of characters: 1) mottled sepals and petals; 2) differently shaped ridge of lateral lobes of labellum; 3) broadly obovate midlobe of labellum; and 4) thick, exserted ridge of midlobe of labellum.","APG : Acta phytotaxonomica et geobotanica","YUKAWA TOMOHISA;TANAKA NOBUYUKI;MURATA JIN","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0009410646","http://ci.nii.ac.jp/naid/110007593082","http://ci.nii.ac.jp/naid/110007593082");
