REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","60","3","171","174","2010-03-30","2010","New or Noteworthy Plant Collections from Myanmar (5) : Dendrobium koyamae, a New Species in sect. Formosa (Orchidaceae)","A new species, Dendrobium koyamae Nb. Tanaka, T. Yukawa & J. Murata (Orchidaceae; sect. Formosa), from Myanmar is described and illustrated. It is distinguishable from the closely related D. scabrilingue Lindl., by the larger flowers, obovate petals, rounded, orangish yellow with darker nerves (greenish with darker nerves in D. scabrilingue) lateral lobes of the labellum, and prominently crenulate margins of the midlobe of the labellum.","APG : Acta phytotaxonomica et geobotanica","TANAKA NOBUYUKI;YUKAWA TOMOHISA;MURATA JIN","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0009410647","http://ci.nii.ac.jp/naid/110007593083","http://ci.nii.ac.jp/naid/110007593083");
