REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","64","1","1","13","2013-06-29","2013","Molecular Phylogeny of Trachelospermum (Apocynaceae) in Japan Based on cpDNA and nrDNA Nucleotide Sequences","A molecular phylogenetic analysis of Trachelospermum (Apocynaceae; T. asiaticum, T. jasminoides var. pubescens and T. gracilipes var. liukiuense) in Japan was conducted using cpDNA and nrDNA nucleotide sequences. The cpDNA phylogeny did not agree with morphology-based taxonomy, whereas the ITS phylogenies agree with morphology. These results can be explained by chloroplast capture or incomplete lineage sorting. The molecular data also indicate sequence differentiation in T. gracilipes var. liukiuense between the Bonin and Ryukyu islands. Furthermore, in the ITS sequences of T. jasminoides var. pubescens, additive nucleotide sites were present in the substitution sites between T. asiaticum and T. jasminoides var. pubescens. The results support the possibility of natural hybridization between T. asiaticum and T. jasminoides var. pubescens.","APG : Acta phytotaxonomica et geobotanica","UEMACHI AZUSA;SHIMOMURA TAKASHI","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0010080734","http://ci.nii.ac.jp/naid/110009615074","http://ci.nii.ac.jp/naid/110009615074");
REPLACE INTO multilingual(`key`,language,value,guid) values("title","en","Molecular Phylogeny of Trachelospermum (Apocynaceae) in Japan Based on cpDNA and nrDNA Nucleotide Sequences","http://ci.nii.ac.jp/naid/110009615074");
REPLACE INTO multilingual(`key`,language,value,guid) values("abstract","en","A molecular phylogenetic analysis of Trachelospermum (Apocynaceae; T. asiaticum, T. jasminoides var. pubescens and T. gracilipes var. liukiuense) in Japan was conducted using cpDNA and nrDNA nucleotide sequences. The cpDNA phylogeny did not agree with morphology-based taxonomy, whereas the ITS phylogenies agree with morphology. These results can be explained by chloroplast capture or incomplete lineage sorting. The molecular data also indicate sequence differentiation in T. gracilipes var. liukiuense between the Bonin and Ryukyu islands. Furthermore, in the ITS sequences of T. jasminoides var. pubescens, additive nucleotide sites were present in the substitution sites between T. asiaticum and T. jasminoides var. pubescens. The results support the possibility of natural hybridization between T. asiaticum and T. jasminoides var. pubescens.","http://ci.nii.ac.jp/naid/110009615074");
