REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","64","2","107","111","2013-08-30","2013","Peliosanthes brevicoronata (Asparagaceae), a New Species from Thailand","Based on plants from Huai Kha Khaeng Wildlife Sanctuary, Uthai Thani Province, Thailand, we provide the first description of Peliosanthes brevicoronata, sp. nov. (Asparagaceae). The corona of P. brevicoronata is not well developed, a trait shared with P. gracilipes and P. minutiflora, but P. brevicoronata differs from the former species in its densely flowered inflorescence, small pistil, and abaxially greenish purple and adaxially yellow erect tepals, and from the latter by its indistinct aerial stem.","APG : Acta phytotaxonomica et geobotanica","TAMURA MINORU N.;POOPATH MANOP;SIRIMONGKOL SUKONTIP","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0010131829","http://ci.nii.ac.jp/naid/110009657379","http://ci.nii.ac.jp/naid/110009657379");
