REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","64","3","149","153","2013-10-31","2013","Musa kamengensis (Musaceae), a New Species from Arunachal Pradesh, India","A new wild species of banana Musa kamengensis Gogoi & Hakkinen, is described and illustrated. The species is abundant in a 250 sq. km area from Jamiri, Zero point to Kimi point along Sepa Road in West Kameng, Arunachal Pradesh, India. A key to the closely related species is provided.","APG : Acta phytotaxonomica et geobotanica","GOGOI RAJIB;HAKKINEN MARKKU","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0010137609","http://ci.nii.ac.jp/naid/110009661542","http://ci.nii.ac.jp/naid/110009661542");
