REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","61","2","87","92","2010-11-30","2010","A New Species of Piptospatha (Araceae: Schismatoglottideae) from West Kalimantan, Indonesian Borneo","As a result of a botanical expedition made as part of the Heart of Borneo Project, a new rheophytic aroid species, Piptospatha repens (Araceae: Schismatoglottideae) was found on the Sungai (River) Mendalam, a branch of the Sungai Kapuas, Putussibau, West Kalimantan, Indonesia.","APG : Acta phytotaxonomica et geobotanica","OKADA HIROSHI;TSUKAYA HIROKAZU","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0010304203","http://ci.nii.ac.jp/naid/110009804244","http://ci.nii.ac.jp/naid/110009804244");
