REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","65","1","25","28","2014-02-28","2014","Chlorophytum longissimum var. phukhaense (Asparagaceae), a New Variety from Thailand","Based on plants from Doi Phukha, Nan Province, Thailand, we provide the first description of Chlorophytum longissimum var. phukhaense, var. nov. (Asparagaceae). This variety differs from C. longissimum var. longissimum in having a long vertical rhizome and an inflorescence that elongates before anthesis.","APG : Acta phytotaxonomica et geobotanica","TAMURA MINORU N.;POOMA RACHUN","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0010321903","http://ci.nii.ac.jp/naid/110009816920","http://ci.nii.ac.jp/naid/110009816920");
