REPLACE INTO publications(issn,volume,issue,spage,epage,date,year,title,abstract,journal,authors,pdf,url,guid) values("1346-7565","66","2","91","94","2015-06-30","2015","A New Species of Stereospermum (Bignoniaceae) from Southern Viet Nam","A new species of Bignoniaceae, Stereospermum binhchauensis, recently discovered in the Binh Chau - Phuoc Buu Nature Reserve of southern Viet Nam, is described and illustrated. Stereospermum binhchauensis is morphologically similar to S. neuranthum Kurz, but differs in having a longer inflorescence, 30-45 cm long and a yellowish purple, pubescent, longer corolla, 4-5 cm long. A key to the species of Stereospermum in Viet Nam is also provided.","APG : Acta phytotaxonomica et geobotanica","DANG VAN-SON","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0010526097","http://ci.nii.ac.jp/naid/110009974494","http://ci.nii.ac.jp/naid/110009974494");
